mapper.update:
UPDATE notice SET nTitle=#{nTitle},
		nContent=#{nContent}
WHERE nNum=#{nNum}

mapper.select:
SELECT nFileNum, nNum, nSaveFile, nOriginalFile, nFileSize
FROM noticeFile
WHERE nNum=#{nNum}

	SELECT nFileNum, nNum, nSaveFile, nOriginalFile, nFileSize
	FROM noticeFile
    WHERE nFileNum=#{nFileNum}
