mapper.select:
SELECT pName, p.pCateNum, pPrice, pDiscountRate FROM product p JOIN productCategory c ON p.pCateNum = c.pCateNum
AND p.pCateNum = #{pCateNum}
OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY

SELECT storeSubOptNum, storeSubOptName, storeMainOptNum 
FROM storeSubOpt s
WHERE storeMainOptNum = #{storeMainOptNum}

SELECT pImgNum, pImgName, pNum
FROM productImage
WHERE pNum=#{pNum}
